mapper.select:
  
    SELECT t.id, t.cardid, t.name, t.nickname, t.type, t.rarity, t.imageurl, t.iconurl FROM t_tgks_mar_krsmacard t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.CARDID = #{cardId}
    
    
        AND t.NAME LIKE CONCAT(CONCAT('%', #{name}),'%')
    
    
        AND t.NICKNAME LIKE CONCAT(CONCAT('%', #{nickName}),'%')
    
    
        AND t.TYPE = #{type}
    
    
        AND t.RARITY = #{rarity}
    
    
        AND t.IMAGEURL = #{imageUrl}
    
    
        AND t.ICONURL = #{iconUrl}
    


    ORDER BY ${sortSql}
