mapper.select:
  
    SELECT t.id, t.itemid, t.name, t.type, t.status, t.param FROM t_tgks_mar_marzitem t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.ITEMID = #{itemId}
    
    
        AND t.NAME = #{name}
    
    
        AND t.TYPE = #{type}
    
    
        AND t.STATUS = #{status}
    


    ORDER BY ${sortSql}
