mapper.select:
  
    SELECT t.id, t.tgksid, t.type, t.info, t.createtime FROM t_tgks_mar_marzlog t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.TGKSID = #{tgksId}
    
    
        AND t.TYPE = #{type}
    
    
        AND t.INFO = #{info}
    
    
        AND t.CREATETIME = #{createTime}
    


    ORDER BY ${sortSql}

mapper.update:

  UPDATE t_tgks_mar_marzlog SET 
             tgksid = #{tgksId},
             type = #{type},
             info = #{info},
             createtime = #{createTime}
   WHERE ID = #{id}
